import React, { useState, useEffect } from "react";
import TrackerComponent from "./TrackerComponent";
import { useHistory } from "react-router-dom";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import Tooltip from "@material-ui/core/Tooltip";
import OpenInNewIcon from "@material-ui/icons/OpenInNew";
import PublicNavbarComponent from "../Components/PublicNavbarComponent";
import SpinnerComponent from "../Components/SpinnerComponent";
import AddContactForm from "./AddContactForm";
import AddIcon from '@material-ui/icons/Add';
import DoneAllIcon from "@material-ui/icons/DoneAll";
import Fab from "@material-ui/core/Fab";

const axios = require("axios");

function ClientTrackerPage() {
  const [clientObject, setClientObject] = useState([]);

  let history = useHistory();

  useEffect((props) => {
    //alert(JSON.stringify(value));

    const search = window.location.search;
    const params = new URLSearchParams(search);
    const clientUrl = params.get("client");

    //We call the api
    axios
      .get(
        `${process.env.REACT_APP_API_ROOT_URL}/client-management/clients/${clientUrl}`,
        {
          headers: {},
        }
      )
      .then(function (response) {
        setClientObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  }, []);

  return (
    <>
      <div class="container">
        <PublicNavbarComponent />
        <nav class="breadcrumb" aria-label="breadcrumbs">
          <ul>
            <li>
              <a href="/">Home</a>
            </li>
            <li class="is-active">
              <a href="">Client Tracker</a>
            </li>
          </ul>
        </nav>
        <div class="card">
          <div class="card-content">
            {!clientObject.id && <SpinnerComponent />}
            {clientObject.id && (
              <div id="main_index">
                <h2>
                  {clientObject.id + "-" + clientObject.name}{" "}
                  <Tooltip title="Open Client Website">
                    <a
                      href={clientObject.website}
                      rel="noopener noreferrer"
                      target="_blank"
                    >
                      {" "}
                      <OpenInNewIcon style={{ fontSize: 40 }} />
                    </a>
                  </Tooltip>
                </h2>

                <br />
                <h4>
                  <strong>Client Manager:</strong> {clientObject.clientManager}
                </h4>
              </div>
            )}

            <div class="columns">
              <div class="column is-8">
                <div class="field">
                  <div class="control"></div>
                </div>
                <div class="field">
                  <p>
                    <strong> {"TIER " + clientObject.tier} -</strong>{" "}
                    {clientObject.tierDescription}
                  </p>
                </div>

                <div class="field">
                  <AddContactForm clientId={clientObject.id} />
                </div>
              </div>

              <div class="column">
                <div class="field">
                  <p>
                    {/*clientObject.sector + " - " + clientObject.sectorDescription*/}
                    <table
                      className="table table is-fullwidth"
                      id="tableSector"
                    >
                      <tr>
                        <td colspan="3" align="center">
                          Electricity
                        </td>
                      </tr>
                      <tr>
                        <td>Supply</td>
                        <td>Generation</td>
                        <td>Distribution</td>
                      </tr>
                      <tr>
                        <td>
                          {clientObject.sector == 1 && (
                            <td>
                              <DoneAllIcon
                                style={{ color: "#9aae04", fontSize: 40 }}
                              />
                            </td>
                          )}
                        </td>
                        <td>
                          {clientObject.sector == 2 && (
                            <td>
                              <DoneAllIcon
                                style={{ color: "#9aae04", fontSize: 40 }}
                              />
                            </td>
                          )}
                        </td>
                        <td>
                          {clientObject.sector == 3 && (
                            <td>
                              <DoneAllIcon
                                style={{ color: "#9aae04", fontSize: 40 }}
                              />
                            </td>
                          )}
                        </td>
                      </tr>
                    </table>
                    <table
                      className="table table is-fullwidth"
                      id="tableSector"
                    >
                      <tr>
                        <td colspan="3" align="center">
                          Gas
                        </td>
                      </tr>
                      <tr>
                        <td>Supply</td>
                        <td>Generation</td>
                        <td>Distribution</td>
                      </tr>
                      <tr>
                        <td>
                          {clientObject.sector == 4 && (
                            <td>
                              <DoneAllIcon
                                style={{ color: "#9aae04", fontSize: 40 }}
                              />
                            </td>
                          )}
                        </td>
                        <td>
                          {clientObject.sector == 5 && (
                            <td>
                              <DoneAllIcon
                                style={{ color: "#9aae04", fontSize: 40 }}
                              />
                            </td>
                          )}
                        </td>
                        <td>
                          {clientObject.sector == 6 && (
                            <td>
                              <DoneAllIcon
                                style={{ color: "#9aae04", fontSize: 40 }}
                              />
                            </td>
                          )}
                        </td>
                      </tr>
                    </table>
                  </p>
