import React from 'react';
import { useState, useEffect } from 'react';
import scroller from 'react-scroll';
import NavbarComponent from '../Components/NavbarComponent';
import EditUserSection from '../Sections/EditUserSection';
import MUIDataTable from "mui-datatables";
const axios = require('axios');



const options = {
    filterType: 'checkbox',
};

function IndexPage() {
    const [result, setResult] = useState([]);
    const [user, setUser] = useState([]);


    const clickEditUser = () => {
        //alert(JSON.stringify(value));


        //We call the api
        axios.get(`http://localhost:8011/users-management/users/alvaro`, {
            headers: {
                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token')
            }
        }
        )
            .then(function (response) {

                setUser(response.data);

                // Somewhere else, even another file
                scroller.scrollTo('myScrollToElement', {

                    duration: 1000,
                    delay: 200,
                    smooth: true,
                    containerId: 'ContainerElementID',
                    offset: 50 // Scrolls to element + 50 pixels down the page
                })

            })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }

    const columns = [

        {
            name: "id",
            label: "ID",
            options: {
                filter: true
            }
        },
        {
            name: "username",
            label: "USERNAME",
            options: {
                filter: true
            }
        },
        {
            name: "firstName",
            label: "NAME",
            options: {
                filter: true
            }
        },
        {
            name: "lastName",
            label: "SURNAME",
            options: {
                filter: true
            }
        },
        {
            name: "mobile",
            label: "MOBILE PHONE",
            options: {
                filter: true
            }
        },
        {
            name: "company",
            label: "COMPANY",
            options: {
                filter: true
            }
        },
        {
            name: "isActive",
            label: "IS ACTIVE",
            options: {
                filter: true
            }
        },
        {
            name: "",

            options: {
                customBodyRender: (value, tableMeta, updateValue) => {
                    return (
                        <>
                            <button class="button is-danger is-outlined" onClick={() => clickEditUser()}>Edit</button>

                        </>
                    );
                }
            }
        },

    ];



    const fetchData = async () => {
        axios.get(`http://localhost:8011/users-management/users`, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token'),

            }

        })
            .then(function (response) {

                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);

            })
            .finally(function () {
                // always executed
            });


    };
    useEffect(() => { fetchData() }, []);


    return (
        <>
            <NavbarComponent />
            <div className='container'>
                {JSON.stringify(user)}
                <div className='row'>
                    <div className='col'>
                        <p>
                            Lorem ipsum dolor sit amet, eum exerci praesent tractatos cu.
                            Quo at vero debitis disputationi, no percipit maluisset dignissim quo.
                            Mea tale harum ut, partem euismod quo id, cu eum ipsum vocent. Sit no stet corpora.
                            Phaedrum definiebas an mel, pri ad quodsi fabellas patrioque, vix omnis officiis maluisset no.
                            Altera praesent duo ex, ei mei erant tempor causae, quo noster facilis ne. Minim persequeris pri no, illud tollit recusabo ne vim, idque partem ne sea.
                        </p>
                    </div>
                    <br />

                    <div className='col is-three-quarters'>
                        <MUIDataTable
                            title={"Employee List"}
                            data={result}
                            columns={columns}
                            options={options}
                        />
                    </div>

                </div>
            </div>

            <section id='index_section'>
                <EditUserSection user={user} />
            </section>

        </>
    );
}
export default IndexPage;
