import React, { useState, useEffect } from 'react';
import TrackerComponent from './TrackerComponent';
import { useHistory } from "react-router-dom";
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import Tooltip from '@material-ui/core/Tooltip';
import OpenInNewIcon from '@material-ui/icons/OpenInNew';
import PublicNavbarComponent from '../Components/PublicNavbarComponent';
import ClientTrackerChart from './ClientTrackerChart';


const axios = require('axios');

function ClientTrackerPage() {
    const [clientObject, setClientObject] = useState([]);


    let history = useHistory();

    useEffect((props) => {
        //alert(JSON.stringify(value));

        const search = window.location.search;
        const params = new URLSearchParams(search);
        const clientUrl = params.get('client');

        //We call the api
        axios.get(`http://localhost:8011/client-management/clients/${clientUrl}`, {
            headers: {

            }
        }
        )
            .then(function (response) {
                setClientObject(response.data);


            })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }, []);



    const handleChangeUserObject = (e) => {
        setClientObject(
            { ...clientObject, [e.target.name]: e.target.value }
        )
    }
    return (
      <>
        <div class="container">
          <PublicNavbarComponent />
          <nav class="breadcrumb" aria-label="breadcrumbs">
            <ul>
              <li>
                <a href="/">Home</a>
              </li>
              <li class="is-active">
                <a href="/client_tracker" aria-current="page">
                  Client Tracker
                </a>
              </li>
            </ul>
          </nav>
          <div id="main_index">
            <h2>Client Tracker</h2>
          </div>

          <div class="card-content">
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">ID</label>
                  <div class="control">
                    <input
                      class="input is-primary"
                      name="id"
                      id="id"
                      value={clientObject.id}
                      type="text"
                      maxLength="50"
                      required
                      readOnly
                    />
                  </div>
                </div>
              </div>

              <div class="column">
                <div class="field">
                  <label class="label">Name</label>
                  <div class="control">
                    <input
                      class="input is-primary"
                      name="name"
                      id="name"
                      value={clientObject.name}
                      type="text"
                      maxLength="50"
                      readOnly
                    />
                  </div>
                </div>
              </div>
            </div>
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Website</label>
                  <div class="control">
                    <a
                      href={clientObject.website}
                      rel="noopener noreferrer"
                      target="_blank"
                    >
                      {clientObject.website}{" "}
                    </a>
                    <OpenInNewIcon />
                  </div>
                </div>
              </div>

              <div class="column">
                <div class="field">
                  <label class="label">Client Manager</label>
                  <div class="control">
                    <input
                      class="input is-primary"
                      name="clientManager"
                      id="clientManager"
                      value={clientObject.clientManager}
                      type="text"
                      maxLength="50"
                      readOnly
                    />
                  </div>
                </div>
              </div>
            </div>

            <div class="columns">
              <div class="column">
                <div class="field is-grouped">
                  <div class="control">
                    <button
                      class="button is-text"
                      onClick={() => history.push("/")}
                    >
                      <Tooltip title="back">
                        <ArrowBackIcon />
                      </Tooltip>
                    </button>
                  </div>
                  <div class="control">
                    <button
                      class="button is-primary"
                      onClick={() =>
                        history.push(`/add_track?client=${clientObject.id}`)
                      }
                    >
                      Add Activity
                    </button>
                  </div>
                </div>
              </div>
            </div>
            <div class="columns">
              <div class="column">
                </div>
                <ClientTrackerChart/>
                </div>
          </div>

          <div class="columns is-vcentered">
            <div class="column">
              <TrackerComponent />
            </div>

          </div>

          <br />
          <br />
          <br />
          <br />
          </div>
      </>
    );
}

export default ClientTrackerPage;
