import React, { useState,useEffect} from "react";
import NavbarComponent from "../Components/NavbarComponent";
import { useHistory } from "react-router-dom";
import ErrorIcon from '@material-ui/icons/Error';
import Alert from '@material-ui/lab/Alert';

const axios = require("axios");

let flag = true;

function NewClientPage() {
  const [clientObject, setClientObject] = useState([]);
  const [tierObject, setTierObject] = useState([]);
  const [sectorObject, setSectorObject] = useState([]);
  const [sending, setSending] = useState(false);


  let history = useHistory();

  const handleChangeClientObject = (e) => {
    setClientObject({ ...clientObject, [e.target.name]: e.target.value });
  };

  const handleSubmit = (evt) => {

      setSending(true);
      flag = true;

    if (!clientObject.name) {
        flag = false;
    }
    if (!clientObject.website) {
        flag = false;
    }
    if (!clientObject.clientManager) {
        flag = false;
    }


    var params = new URLSearchParams();
    params.append("name", clientObject.name);
    params.append("website", clientObject.website);
    params.append("clientManager", clientObject.clientManager);


    if (flag) {

      axios
        .post(`http://localhost:8011/client-management/clients`, params, {
          headers: {
            Authorization: "Bearer " + sessionStorage.getItem("user_token")
          },
        })
        .then(function (response) {
          history.push("/client-mng");
        })
        .catch(function (error) {
          // handle error
          console.log(error);
        })
        .finally(function () {
          // always executed
        });
    }
  };

  useEffect((props) => {

    //We call the api
    axios
      .get(`http://localhost:8011/tier-manager/tier`, {
        headers: {
          Authorization: "Bearer " + sessionStorage.getItem("user_token")
        },
      })
      .then(function (response) {
        setTierObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  }, []);

  return (
    <>
      <div class="container">
        <NavbarComponent />
        <nav class="breadcrumb" aria-label="breadcrumbs">
          <ul>
            <li>
              <a href="/index">Home</a>
            </li>
            <li class="is-active">
              <a href="/new-client" aria-current="page">
                New Client
              </a>
            </li>
          </ul>
        </nav>
        <div id="main_index">
          <h2>New Client</h2>
        </div>
        <div class="card">
          <div class="card-content">
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Name</label>
                  <div class="control">
                    <input
                      class="input"
                      name="name"
                      id="name"
                      type="text"
                      placeholder="name"
                      onChange={(e) => handleChangeClientObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !clientObject.name && (
                      <>
                      <Alert variant="outlined" severity="error">Mandatory Field</Alert>
                      </>
                   )}

                  </div>
                </div>
              </div>

              <div class="column">
                <div class="field">
                  <label class="label">Tier</label>
                  <div class="control">
                  <div class="select is-fullwidth">
                      <select
                        name="tier"
                        id="tier"
                        onChange={(e) => handleChangeClientObject(e)}
                        required
                      >
                        <option selected value="">
                          Select
                        </option>

                        {tierObject.map(function (item) {
                        return <option value={item.id}>{item.tier_title}</option>;
                      })}
                      </select>
                      </div>
                     {sending && !clientObject.tier && (
                      <>
                      <Alert variant="outlined" severity="error">Mandatory Field</Alert>
                      </>
                   )}

                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Sector</label>
                  <div class="control">
                  <div class="select select is-fullwidth">

                      <select
                        name="sector"
                        id="sector"
                        onChange={(e) => handleChangeClientObject(e)}
                        required
                      >
                        <option selected value="">
                          Select
                        </option>
                        <option value="ELECTRICITY - ">Electricity - </option>
                        <option value="ELECTRICITY - ">Electricity - </option>
                        <option value="ELECTRICITY - ">Electricity - </option>
                        <option value="GAS - ">Gas - </option>
                        <option value="GAS - ">Gas - </option>
                        <option value="GAS - ">Gas - </option>
                      </select>
                      </div>
                     {sending && !clientObject.tier && (
                      <>
                      <Alert variant="outlined" severity="error">Mandatory Field</Alert>
                      </>
                   )}

                  </div>
                </div>
              </div>

              </div>
              <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Client Manager</label>
                  <div class="control">
                    <input
                      class="input"
                      name="clientManager"
                      id="clientManager"
                      type="text"
                      placeholder="name"
                      onChange={(e) => handleChangeClientObject(e)}
                      maxLength="50"
                      required
                    />
                     {sending && !clientObject.clientManager && (
                       <>
                       <Alert variant="outlined" severity="error">Mandatory Field</Alert>
                       </>
                    )}

                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Website</label>
                  <div class="control">
                    <input
                      class="input"
                      name="website"
                      id="website"
                      type="text"
                      placeholder="website"
                      onChange={(e) => handleChangeClientObject(e)}
