import React, { useState } from 'react';



function EditUserSection(props) {
    const [userObject, setUserObject] = useState(props.userToEdit);


    const handleChangeUserObject = (e) => {
        setUserObject(
            { [e.target.name]: e.target.value }
        )
    }

    const handleSubmit = (evt) => {
        evt.preventDefault();
        alert(`Submitting Name ${JSON.stringify(userObject)}`)
    }

    return (
        <div>
            <div class='container'>
                <div class='columns'>
                    <div class='column is-two-thirds'>
                        <div id='main_index'>
                            <h2>Edit User</h2>
                        </div>
                        <small>
                            <p>
                                {JSON.stringify(userObject)}
                            </p>
                        </small>


                        <div class="field">
                            <label class="label">Name</label>
                            <div class="control">
                                <input class="input" value={userObject.firstName} type="text" onChange={e => handleChangeUserObject(e)} />
                            </div>
                            <label class="label">Surname</label>
                            <div class="control">
                                <input class="input" value={userObject.lastName} type="text" onChange={e => handleChangeUserObject(e)} />
                            </div>
                            </div>

                            <div class="field">
                                <label class="label">Username</label>
                                <div class="control">
                                    <input class="input" value={userObject.username} type="text" onChange={e => handleChangeUserObject(e)}/>

                                </div>

                                </div>


                                </div>
                            </div>

                        </div>
                    </div>


    );
}

export default EditUserSection;
