import React, { useState, useEffect } from "react";
import TrackerComponent from "./TrackerComponent";
import { useHistory } from "react-router-dom";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import Tooltip from "@material-ui/core/Tooltip";
import OpenInNewIcon from "@material-ui/icons/OpenInNew";
import PublicNavbarComponent from "../Components/PublicNavbarComponent";
import ClientTrackerChart from "./ClientTrackerChart";
import SpinnerComponent from "../Components/SpinnerComponent";
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import DeleteIcon from '@material-ui/icons/Delete';
import AddContactForm from '../Components/AddContactForm';
import RemoveCircleOutlineIcon from '@material-ui/icons/RemoveCircleOutline';
const axios = require("axios");

function ClientTrackerPage() {
  const [clientObject, setClientObject] = useState([]);
  const [contactsObject, setContactsObject] = useState([]);
  const [showContactForm, setShowContactForm] = useState(false);


  let history = useHistory();

  useEffect((props) => {
    //alert(JSON.stringify(value));

    const search = window.location.search;
    const params = new URLSearchParams(search);
    const clientUrl = params.get("client");

    //We call the api
    axios
      .get(`http://localhost:8011/client-management/clients/${clientUrl}`, {
        headers: {},
      })
      .then(function (response) {
        setClientObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });

    axios
      .get(`http://localhost:8011/contact-management/contacts/${clientUrl}`, {
        headers: {},
      })
      .then(function (response) {
        setContactsObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  }, []);


  return (
    <>
      <div class="container">
        <PublicNavbarComponent />
        {!clientObject.id && <SpinnerComponent />}
        {clientObject.id && (
          <div id="main_index">
            <h2>{clientObject.id + "-" + clientObject.name}</h2>
            <h4>
              <strong>Client Manager:</strong> {clientObject.clientManager}
            </h4>
          </div>
        )}

        <div class="card-content">
          <div class="columns">
            <div class="column">
              <div class="field">
                <div class="control">
                  <a
                    href={clientObject.website}
                    rel="noopener noreferrer"
                    target="_blank"
                  >
                    {clientObject.website}{" "}
                  </a>
                  <OpenInNewIcon />
                </div>
              </div>
              <div class="field">
                <div class="label">Tier</div>
                <div class="control">
                  <p>
                    {clientObject.tier + " - " + clientObject.tierDescription}
                  </p>
                </div>
              </div>
              <div class="field">
                <div class="label">Registration Date</div>
                <div class="control">
                  <p>{clientObject.registrationDate}</p>
                </div>
              </div>

              <div class="field">
                <div class="label">Sector</div>

                <div class="control">
                  <p>
                    {clientObject.sector +
                      " - " +
                      clientObject.sectorDescription}
                  </p>
                </div>
              </div>
              <hr />
              <div class="field">
                <div class="label">Contacts
                <button
                  class="button is-white"
                  onClick={() =>
                    showContactForm ? setShowContactForm(false) : setShowContactForm(true) 
                  }
                >
                  { showContactForm ? <RemoveCircleOutlineIcon /> : <AddCircleOutlineIcon /> }

                </button>
                </div>

                {showContactForm && <AddContactForm />}

                <center>
                  <table className="table is-hoverable">
                    <thead>
                      <td></td>
                      <td>Name</td>
                      <td>Surname</td>
                      <td>Role</td>
                      <td>Comment</td>
                    </thead>
                    <tbody>
                      {contactsObject.map(function (item) {
                        return (
                          <tr>
                            <td>
                              <button
                                class="button is-text"
                                onClick={() =>
                                 alert("delete")}
                              >
                                <DeleteIcon />
                              </button>
                            </td>

                            <td>{item.name}</td>
                            <td>{item.surname}</td>
                            <td>{item.role}</td>
                            <td>{item.comment}</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </center>
              </div>
            </div>

            <div class="column">
              {!clientObject.id && <SpinnerComponent />}
              {clientObject.id && <ClientTrackerChart />}
            </div>
          </div>

          <div class="columns">
            <div class="column">
              <div class="field is-grouped">
                <div class="control">
                  <button
                    class="button is-text"
                    onClick={() => history.push("/")}
                  >
                    <Tooltip title="back">
                      <ArrowBackIcon />
                    </Tooltip>
                  </button>
                </div>
                <div class="control">
                  <button
                    class="button is-primary"
                    onClick={() =>
                      history.push(`/add_track?client=${clientObject.id}`)
                    }
                  >
                    New Activity
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="columns is-vcentered">
          <div class="column">
            <TrackerComponent />
          </div>
        </div>


      </div>
    </>
  );
}

export default ClientTrackerPage;
