                    <input
                      class="input"
                      name="name"
                      id="name"
                      value={clientObject.name}
                      type="text"
                      placeholder="name"
                      onChange={(e) => handleChangeClientObject(e)}
                      onKeyPress={e => handleKeyPress(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !clientObject.name && (
                      <>
                      <Alert variant="outlined" severity="error">Mandatory Field</Alert>
                      </>
                    )}
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Website</label>
                  <div class="control">
                    <input
                      class="input"
                      name="website"
                      id="website"
                      value={clientObject.website}
                      type="text"
                      placeholder="website"
                      onChange={(e) => handleChangeClientObject(e)}
                      onKeyPress={e => handleKeyPress(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !clientObject.website && (
                      <>
                      <Alert variant="outlined" severity="error">Mandatory Field</Alert>
