import React from 'react';
import { useState, useEffect } from 'react';
import scroller from 'react-scroll';
import NavbarComponent from '../Components/NavbarComponent';
import AsideMenuComponent from '../Components/AsideMenuComponent';
import MUIDataTable from "mui-datatables";
import { useHistory } from "react-router-dom";
const axios = require('axios');




const options = {
    filterType: 'dropdown',
    selectableRows: false // turn off checkboxes in rows
};

function IndexPage() {
    const [result, setResult] = useState([]);
    let history = useHistory();

    const goToEdit = () => {

        history.push("/admin");
    }

    const deleteUser = () => {


    }

    const columns = [

        {
            name: "id",
            label: "ID",
            options: {
                filter: true
            }
        },
        {
            name: "username",
            label: "USERNAME",
            options: {
                filter: true
            }
        },
        {
            name: "firstName",
            label: "NAME",
            options: {
                filter: true
            }
        },
        {
            name: "lastName",
            label: "SURNAME",
            options: {
                filter: true
            }
        },
        {
            name: "mobile",
            label: "MOBILE PHONE",
            options: {
                filter: true
            }
        },
        {
            name: "company",
            label: "COMPANY",
            options: {
                filter: true
            }
        },
        {
            name: "isActive",
            label: "IS ACTIVE",
            options: {
                filter: true
            }
        },
        {
            name: "",

            options: {
                customBodyRender: (value, tableMeta, updateValue) => {
                    return (
                        <>
                         <button class="button is-link" onClick={() => deleteUser()}>Delete</button>

                         <button class="button is-danger is-outlined" onClick={() => goToEdit()}>Edit</button>   
                        </>
                    );
                },
                filter: false,
                searchable: false,
                viewColumns: false
            }
        },

    ];



    const fetchData = async () => {
        axios.get(`http://localhost:8011/users-management/users`, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token'),

            }

        })
            .then(function (response) {

                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);

            })
            .finally(function () {
                // always executed
            });


    };
    useEffect(() => { fetchData() }, []);



    return (
        <>
            <div className='container'>
                <NavbarComponent />
              </div>
                <div className='columns'>
                <div className='column'>

                    </div>
                    <div className='column is-3'>
                        <AsideMenuComponent />
                    </div>
                    <div className='column is-7'>

                        <MUIDataTable
                            title={"Employee List"}
                            data={result}
                            columns={columns}
                            options={options}
                        />
                    </div>
                    <div className='column'>

                        </div>
                </div>
                <div className='container'>
                <div className='columns'>
                    <div className='column'>
                        <p>
                            Lorem ipsum dolor sit amet, eum exerci praesent tractatos cu.
                            Quo at vero debitis disputationi, no percipit maluisset dignissim quo.
                            Mea tale harum ut, partem euismod quo id, cu eum ipsum vocent. Sit no stet corpora.
                            Phaedrum definiebas an mel, pri ad quodsi fabellas patrioque, vix omnis officiis maluisset no.
                            Altera praesent duo ex, ei mei erant tempor causae, quo noster facilis ne. Minim persequeris pri no, illud tollit recusabo ne vim, idque partem ne sea.
                        </p>
                    </div>
                </div>
            </div>



        </>
    );
}
export default IndexPage;
