import React from 'react';
import { useState, useEffect } from 'react';

import MUIDataTable from "mui-datatables";
import { useHistory } from "react-router-dom";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CloseIcon from '@material-ui/icons/Close';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';

import Tooltip from '@material-ui/core/Tooltip';
import ScrollToTop from 'react-scroll-up';
const axios = require('axios');

function ClientsSection() {
    const [result, setResult] = useState([]);
    let history = useHistory();

    const goToEdit = (value) => {
        history.push("/client_tracker?client=" + value[0]);
    }

    const options = {
        filterType: 'dropdown',
        onRowClick: (event, rowData) => {

            goToEdit(event);
        },
        selectableRows:false,
        rowsPerPageOptions: [5,10]
    };

    const columns = [


        {
            name: "id",
            label: "ID",
            options: {
                filter: true
            }
        },
        {
            name: "name",
            label: "NAME",
            options: {
                filter: true
            }
        },
        {
            name: "clientManager",
            label: "CLIENT MANAGER",
            options: {
                filter: true,

            }
        },
        {
            name: "registrationDate",
            label: "REGISTRATION DATE",
            options: {
                filter: true,

            }
        },

        {
            name: "lastUpdate",
            label: "LAST UPDATE",
            options: {
                filter: true,

            }
        },

        {
            name: "procurementStatus",
            label: "PROCUREMENT STATUS",
            options: {

                filter: true,

                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },

            }
        },
        {
            name: "linkedinStatus",
            label: "LINKEDIN STATUS",
            options: {

                filter: true,

                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },

            },


        },
        {
            name: "contactStatus",
            label: "CONTACT STATUS",
            options: {

                filter: true,

                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },

            },


        },
        {
            name: "accountIntellyStatus",
            label: "ACCOUNT INTELLIGENCE STATUS",
            options: {

                filter: true,

                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },

            },


        },

        {
            name: "partnerStatus",
            label: "PARTNER STATUS",
            options: {

                filter: true,

                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },

            },

        },
        {
            name: "offeringStatus",
            label: "OFFERING STATUS",
            options: {

                filter: true,

                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },

            },
        },



      /*  {
            name: "isActive",
            label: "ACTIVE",
            options: {
                filter: true,
                customBodyRender: (value) => {

                    if(value === "Y"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                }

            }
        },  */

    ];


    const fetchData = async () => {
        axios.get(`${process.env.REACT_APP_API_ROOT_URL}/client-management/clients`, {
            headers: {


            }

        })
            .then(function (response) {

                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log("Error Fetching Data: "+error);
                history.push("/");


            })
            .finally(function () {
                // always executed
            });


    };
    useEffect(() => { fetchData() }, []);

    return (
        <>
           <div className="columns is-centered">

                    <MUIDataTable
                        title={"Clients"}
                        data={result}
                        columns={columns}
                        options={options}
                    />
        </div>
        <div class="columns">
            <div class="column is-four-fifths">

            </div>
            <div class="column">
                <ScrollToTop showUnder={1}>
                    <Tooltip title="Scroll Up">

                            <span>
                                <KeyboardArrowUpIcon  style={{ fontSize: 120 }} />
                            </span>

                    </Tooltip>
                </ScrollToTop>
            </div>

        </div>
        <hr />
        </>
    );
}
export default ClientsSection;
