import React, { useState, useEffect } from 'react';
import TrackerComponent from './TrackerComponent';
import { useHistory } from "react-router-dom";
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import Tooltip from '@material-ui/core/Tooltip';
import OpenInNewIcon from '@material-ui/icons/OpenInNew';
import PublicNavbarComponent from '../Components/PublicNavbarComponent';
import bulmaCalendar from '../../node_modules/bulma-calendar/dist/js/bulma-calendar.min.js';

const axios = require('axios');

function AddTrackPage() {
    const [trackObject, setTrackObject] = useState([]);
    const [areaList, setAreaList] = useState([]);
    const [clientId, setClientID] = useState();

    let history = useHistory();

    useEffect((props) => {

         // Initialize all input of date type.
    const calendars = bulmaCalendar.attach('[type="date"]', {});

    // Loop on each calendar initialized
    calendars.forEach((calendar) => {
      // Add listener to date:selected event
      calendar.on('date:selected', (date) => {
        console.log(date);
      });
    });

    // To access to bulmaCalendar instance of an element
    // eslint-disable-next-line no-undef
    const element = document.querySelector('#dateAction');
    if (element) {
      // bulmaCalendar instance is available as element.bulmaCalendar
      element.bulmaCalendar.on('select', (datepicker) => {
        console.log(datepicker.data.value());
      });
    }
        //alert(JSON.stringify(value));

        const search = window.location.search;
        const params = new URLSearchParams(search);
        const clientUrl = params.get('client');

        setClientID(clientUrl);

        //We call the api
        axios.get(`http://localhost:8011/tracker-areas/areas`, {
            headers: {

            }
        }
        )
            .then(function (response) {
                setAreaList(response.data);


            })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }, []);


    const handleChangeTrackObject = (e) => {
        setTrackObject(
            { ...trackObject, [e.target.name]: e.target.value }
        )
    }

    const handleSubmit = (evt) => {

        var params = new URLSearchParams();
        params.append('client_id', clientId);
        params.append('area_id', trackObject.area_id);
        params.append('dateAction', trackObject.dateAction);
        params.append('name', trackObject.name);
        params.append('role', trackObject.role);
        params.append('comment', trackObject.comment);


        axios.post(`http://localhost:8011/client-tracker/tracks`, params, {
            headers: {



            }
        }).then(function (response) {
            history.push(`/client_tracker?client=${clientId}`);

        })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }

    return (
        <>
              <div class='container'>
               <PublicNavbarComponent />
                <nav class="breadcrumb" aria-label="breadcrumbs">
                    <ul>
                        <li><a href="/">Home</a></li>
                        <li><a href="/client_tracker">Client Tracker</a></li>
                        <li class="is-active"><a href="">Add Tracker</a></li>
                    </ul>
                </nav>
                <div id='main_index'>
                    <h2>Add Client Tracker</h2>

                </div>
                <div class="card">
                    <div class="card-content">
                        <div class='columns'>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Client ID</label>
                                    <div class="control is-primary">
                                        <input class="input" name="client_id" id="client_id" value={clientId} type="text" maxLength='50' required readOnly />
                                    </div>
                                </div>
                            </div>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Area</label>
                                    <div class="select">
                                    <select name='area_id' id='area_id' onChange={e => handleChangeTrackObject(e)}>
                                        {
                                            areaList.map(function(item){
                                                return(
                                                    <option value={item.id}>{item.title}</option>
                                                  )
                                            })
                                        }
                                        </select>
                                    </div>
                                </div>
                            </div>
                            <div class='column'>
                                <div class="field">
                                    <label class="label">Date</label>
                                    <div class="control">
                                        <input class="input" name="dateAction" id="dateAction" type="date" onChange={e => handleChangeTrackObject(e)}/>

                                    </div>
                                </div>
                            </div>
                            </div>
                        <div class='columns'>
                            <div class='column'>
                                <div class="field">
                                    <label class="label">Name</label>
                                    <div class="control">
                                        <input class="input" name="name" id="name" type="text" maxLength='50'onChange={e => handleChangeTrackObject(e)}/>
                                    </div>
                                </div>
                            </div>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Role</label>
                                    <div class="control">
                                        <input class="input" name="role" id="role"  type="text" maxLength='50' onChange={e => handleChangeTrackObject(e)}/>
                                    </div>
                                </div>
                            </div>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Comment</label>
                                    <div class="control">
                                        <input class="input" name="comment" id="comment"  type="text" maxLength='50' onChange={e => handleChangeTrackObject(e)}/>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div class='columns is-centered'>
                        <center>
                            <div class='column'>

                                <div class="field is-grouped">

                                    <div class="control">                        
                                        <button class="button is-text" onClick={() => history.goBack()}><Tooltip title="back"><ArrowBackIcon /></Tooltip></button>
                                    </div>
                                    <div class="control">
                                        <button class="button is-primary" onClick={() => handleSubmit()} >Submit</button>
                                    </div>

                                </div>

                            </div>
                            </center>
                        </div>

                    </div>


                    </div>
            </div>
        </>
    );
}

export default AddTrackPage;
