import React, { useState, useEffect } from "react";
import Alert from "@material-ui/lab/Alert";
import DeleteIcon from "@material-ui/icons/Delete";
import AddIcon from "@material-ui/icons/Add";
import RemoveIcon from "@material-ui/icons/Remove";
import Fab from "@material-ui/core/Fab";
import Tooltip from "@material-ui/core/Tooltip";
const axios = require("axios");
let flag = true;

function AddOpportunityForm(props) {
  const [opportunityObject, setOpportunityObject] = useState([]);
  const [auxOpportunityObject, setAuxOpportunityObject] = useState([]);
  const [sending, setSending] = useState(false);
  const [showOpportunityForm, setShowOpportunityForm] = useState(false);

  const handleChangeContactObject = (e) => {
    setAuxOpportunityObject({
      ...auxOpportunityObject,
      [e.target.name]: e.target.value,
    });
  };

  const fetchData = () => {
    const search = window.location.search;
    const params = new URLSearchParams(search);
    const clientUrl = params.get("client");

    axios
      .get(
        `${process.env.REACT_APP_API_ROOT_URL}/opportunity-management/opportunities/${clientUrl}`,
        {
          headers: {
            Authorization: "Bearer " + sessionStorage.getItem("user_token"),
          },
        }
      )
      .then(function (response) {
        setContactsObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  };

  useEffect((props) => {
    fetchData();
  }, []);

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSubmit();
    }
  };

  const handleSubmit = (evt) => {
    setSending(true);

    flag = true;
    var params = new URLSearchParams();

    if (!auxOpportunityObject.name) {
      flag = false;
    }
    if (!auxOpportunityObject.surname) {
      flag = false;
    }
    if (!auxOpportunityObject.role) {
      flag = false;
    }

    if (!auxOpportunityObject.phoneNumber) {
      flag = false;
    }

    if (!auxOpportunityObject.emailAddress) {
      flag = false;
    }

    if (!auxOpportunityObject.comment) {
      flag = false;
    }
    if (!props.clientId) {
      flag = false;
    }

    if (flag) {
      params.append("client_id", props.clientId);

      params.append("surname", auxOpportunityObject.surname);
      params.append("name", auxOpportunityObject.name);
      params.append("role", auxOpportunityObject.role);
      params.append("phoneNumber", auxOpportunityObject.phoneNumber);
      params.append("emailAddress", auxOpportunityObject.emailAddress);
      params.append("comment", auxOpportunityObject.comment);

      axios
        .post(
          `${process.env.REACT_APP_API_ROOT_URL}/opportunity-management/opportunities`,
          params,
          {
            headers: {
              Authorization: "Bearer " + sessionStorage.getItem("user_token"),
            },
          }
        )
        .then(function (response) {
          fetchData();
        })
        .catch(function (error) {
          // handle error
        })
        .finally(function () {
          // always executed
        });
    }
  };
  const handleDeleteContacts = (id) => {
    axios
      .delete(
        `${process.env.REACT_APP_API_ROOT_URL}/opportunity-management/opportunities/${id}`,
        {
          headers: {
            Authorization: "Bearer " + sessionStorage.getItem("user_token"),
          },
        }
      )
      .then(function (response) {
        fetchData();
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  };

  return (
    <>
      <div class="label">
      Opportunities{" "}
        {showOpportunityForm ? (
          <Tooltip placement="right" title="Hide New Opportunity Form">
            <Fab
              color="primary"
              aria-label="add"
              onClick={() =>
                showOpportunityForm
                  ? setShowOpportunityForm(false)
                  : setShowOpportunityForm(true)
              }
            >
              {" "}
              <RemoveIcon style={{ color: "white" }} />
            </Fab>
          </Tooltip>
        ) : (
          <Tooltip placement="right" title="Show New Contact Form">
            <Fab
              color="primary"
              aria-label="add"
              onClick={() =>
                showContactForm
                  ? setShowContactForm(false)
                  : setShowContactForm(true)
              }
            >
              {" "}
              <AddIcon style={{ color: "white" }} />
            </Fab>
          </Tooltip>
        )}
      </div>
      {showContactForm ? (
        <>
          <div className="columns">
            <div className="column">
              <label className="label">Name</label>
              <input
                className="input"
                name="name"
                id="name"
                placeholder="name"
                type="text"
                onChange={(e) => handleChangeContactObject(e)}
                onKeyPress={(e) => handleKeyPress(e)}
                maxLength="20"
              />

              {sending && !auxOpportunityObject.name && (
                <>
                  <Alert variant="outlined" severity="error">
                    Mandatory Field
                  </Alert>
                </>
              )}
            </div>

            <div className="column">
              <label className="label">Surname</label>
              <input
                class="input"
                name="surname"
                id="surname"
                placeholder="surname"
                type="text"
                onChange={(e) => handleChangeContactObject(e)}
                onKeyPress={(e) => handleKeyPress(e)}
                maxLength="20"
              />

              {sending && !auxOpportunityObject.surname && (
                <>
                  <Alert variant="outlined" severity="error">
                    Mandatory Field
                  </Alert>
                </>
              )}
            </div>

            <div className="column">
              <label className="label">Role</label>
              <input
                class="input"
                name="role"
                id="role"
                placeholder="role"
                type="text"
                onChange={(e) => handleChangeContactObject(e)}
                onKeyPress={(e) => handleKeyPress(e)}
                maxLength="20"
              />

              {sending && !auxOpportunityObject.role && (
                <>
                  <Alert variant="outlined" severity="error">
                    Mandatory Field
                  </Alert>
                </>
              )}
            </div>
          </div>
          <div className="columns">
            <div className="column">
              <label className="label">Phone Number</label>
              <input
                class="input"
                name="phoneNumber"
                id="phoneNumber"
                placeholder="phone number"
                type="tel"
                onChange={(e) => handleChangeContactObject(e)}
                onKeyPress={(e) => handleKeyPress(e)}
                maxLength="20"
              />

              {sending && !auxOpportunityObject.role && (
                <>
                  <Alert variant="outlined" severity="error">
                    Mandatory Field
                  </Alert>
                </>
              )}
            </div>

            <div className="column">
              <label className="label">Email Address</label>
              <input
                class="input"
                name="emailAddress"
                id="emailAddress"
                placeholder="email address"
                type="email"
                onChange={(e) => handleChangeContactObject(e)}
                onKeyPress={(e) => handleKeyPress(e)}
                maxLength="50"
              />

              {sending && !auxOpportunityObject.role && (
                <>
                  <Alert variant="outlined" severity="error">
                    Mandatory Field
                  </Alert>
                </>
              )}
            </div>
            <div className="column">
              <label className="label">Aditional Info</label>
              <input
                class="input"
                name="comment"
                id="comment"
                placeholder="Aditional Info"
                type="text"
                onChange={(e) => handleChangeContactObject(e)}
                onKeyPress={(e) => handleKeyPress(e)}
                maxLength="50"
              />

              {sending && !auxOpportunityObject.comment && (
                <>
                  <Alert variant="outlined" severity="error">
                    Mandatory Field
                  </Alert>
                </>
              )}
            </div>
          </div>
          <center>
            <button class="button is-black" onClick={() => handleSubmit()}>
              Add Contact
            </button>
          </center>
        </>
      ) : (
        <></>
      )}

      <center>
        <div class="columns">
          <div class="column">
            <table className="table is-hoverable">
              <thead>
                <td></td>
                <td>Name</td>
                <td>Surname</td>
                <td>Role</td>
                <td>Phone Number</td>
                <td>Email Address</td>
                <td>Aditional Info</td>
              </thead>
              <tbody>
                {opportunitiesObject.length > 0 ? (
                  contactsObject.map(function (item) {
                    return (
                      <tr>
                        <td>
                          <button
                            class="button is-text"
                            onClick={() => handleDeleteContacts(item.id)}
                          >
                            <DeleteIcon />
                          </button>
                        </td>

                        <td>{item.name}</td>
                        <td>{item.surname}</td>
                        <td>{item.role}</td>
                        <td>{item.phoneNumber}</td>
                        <td>{item.emailAddress}</td>
                        <td>{item.comment}</td>
                      </tr>
                    );
                  })
                ) : (
                  <td colspan="7">
                    <center>No results found</center>
                  </td>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </center>
    </>
  );
}

export default AddOpportunityForm;
