import React from 'react';
import { useState, useEffect } from 'react';
import scroller from 'react-scroll';
import NavbarComponent from '../Components/NavbarComponent';
import AsideMenuComponent from '../Components/AsideMenuComponent';
import MUIDataTable from "mui-datatables";
import { useHistory } from "react-router-dom";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CloseIcon from '@material-ui/icons/Close';

const axios = require('axios');

function ClientsSection() {
    const [result, setResult] = useState([]);
    let history = useHistory();

    const goToEdit = (value) => {
        history.push("/admin?client=" + value[1]);
    }

    const options = {
        filterType: 'dropdown',
        rowHover: true,
        onRowsDelete: (rowsDeleted) => {
            const rowsToDelete = rowsDeleted.data.map((d) => result[d.dataIndex]);
            handleDelete(rowsToDelete);
        },
        onRowClick: (event, rowData) => {
            goToEdit(event);
        },
        rowsPerPageOptions: [5,10]
    };

    const columns = [


        {
            name: "id",
            label: "ID",
            options: {
                filter: true
            }
        },
        {
            name: "name",
            label: "NAME",
            options: {
                filter: true
            }
        },
        {
            name: "clientManager",
            label: "CLIENT MANAGER",
            options: {
                filter: true,

            }
        },
        {
            name: "website",
            label: "WEBSITE",
            options: {
                filter: true,
                customBodyRender: (value) => {


                    return (
                    <a href={value} rel="noopener noreferrer" target="_blank">{value}</a>
                      );
                    }
                }

        },

        {
            name: "procurementStatus",
            label: "PROCUREMENT STATUS",
            options: {
                filter: true,
                display: true,
                customBodyRender: (value) => {

                    if(value === "OK"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                },
                setCellHeaderProps: () => ({ style: {display: 'flex', justifyContent: 'center', flexDirection: 'row-reverse', borderBottom: 'none'}})
            }
        },


      /*  {
            name: "isActive",
            label: "ACTIVE",
            options: {
                filter: true,
                customBodyRender: (value) => {

                    if(value === "Y"){
                    return (
                        <CheckCircleIcon style={{ color: '#64A205' }}/>
                      );
                    }else{
                        return (
                        <CloseIcon style={{ color: '#C71E03' }}/>
                        );
                    }
                }

            }
        },  */

    ];

    const handleDelete = (recordsToDelete) => {


        var result = recordsToDelete.map(function (item) {
            return item.id;
        }).join(', ');

        axios.delete(`http://localhost:8011/client-management/clients/${result}`, {
            headers: {

                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token')

            }
        }).then(function (response) {
            fetchData();

        })
            .catch(function (error) {

                console.log("Error Fetching Data: "+error);
                history.push("/");

            })
            .finally(function () {
                // always executed
            });

    }


    const fetchData = async () => {
        axios.get(`http://localhost:8011/client-management/clients`, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token'),

            }

        })
            .then(function (response) {

                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log("Error Fetching Data: "+error);
                history.push("/");


            })
            .finally(function () {
                // always executed
            });


    };
    useEffect(() => { fetchData() }, []);

    return (
        <>

            <div className='columns'>
                <div className='column'>

                    <MUIDataTable
                        title={"Clients"}
                        data={result}
                        columns={columns}
                        options={options}
                    />
                </div>

            </div>


        </>
    );
}
export default ClientsSection;
