import React, { useState, useEffect } from "react";
import TrackerComponent from "./TrackerComponent";
import { useHistory } from "react-router-dom";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import Tooltip from "@material-ui/core/Tooltip";
import OpenInNewIcon from "@material-ui/icons/OpenInNew";
import PublicNavbarComponent from "../Components/PublicNavbarComponent";
import SpinnerComponent from "../Components/SpinnerComponent";
import AddContactForm from "./AddContactForm";
const axios = require("axios");

function ClientTrackerPage() {
  const [clientObject, setClientObject] = useState([]);

  let history = useHistory();

  useEffect((props) => {
    //alert(JSON.stringify(value));

    const search = window.location.search;
    const params = new URLSearchParams(search);
    const clientUrl = params.get("client");

    //We call the api
    axios
      .get(
        `${process.env.REACT_APP_API_ROOT_URL}/client-management/clients/${clientUrl}`,
        {
          headers: {},
        }
      )
      .then(function (response) {
        setClientObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  }, []);

  return (
    <>
      <div class="container">
        <PublicNavbarComponent />
        <nav class="breadcrumb" aria-label="breadcrumbs">
          <ul>
            <li>
              <a href="/">Home</a>
            </li>
            <li class="is-active">
              <a href="">Client Tracker</a>
            </li>
          </ul>
        </nav>
        {!clientObject.id && <SpinnerComponent />}
        {clientObject.id && (
          <div id="main_index">
            <h2>
              {clientObject.id + "-" + clientObject.name}{" "}
              <Tooltip title="Open Client Website">
              <a
                href={clientObject.website}
                rel="noopener noreferrer"
                target="_blank"
              >
                {" "}
                <OpenInNewIcon style={{ fontSize: 40 }}/>
              </a>
              </Tooltip>
            </h2>

            <br />
            <h4>
              <strong>Client Manager:</strong> {clientObject.clientManager}
            </h4>
          </div>
        )}

        <div class="card-content">
          <div class="columns">
            <div class="column is-8">
              <div class="field">
                <div class="control"></div>
              </div>
              <div class="field">
              <p><div class="label">Tier</div>


                    {clientObject.tier + " - " + clientObject.tierDescription}
                  </p>

              </div>

              <div class="field">
                <AddContactForm clientId={clientObject.id} />
              </div>
            </div>

            <div class="column">
              <div class="field">
              <p> <div class="label">Registration Date</div>

                  {clientObject.registrationDate}</p>

              </div>

              <div class="field">
                <div class="label">Sector</div>


                  <p>
                    {clientObject.sector +
                      " - " +
                      clientObject.sectorDescription}
                  </p>

              </div>
            </div>
          </div>
          <hr />
          <div class="columns">
            <div class="column">
              <div class="field is-grouped">
                <div class="control">
                  <button
                    class="button is-text"
                    onClick={() => history.push("/")}
                  >
                    <Tooltip title="back">
                      <ArrowBackIcon />
                    </Tooltip>
                  </button>
                </div>
                <div class="control">
                  <button
                    class="button is-primary"
                    onClick={() =>
                      history.push(`/add_track?client=${clientObject.id}`)
                    }
                  >
                    New Activity
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="columns is-vcentered">
          <div class="column">
            <TrackerComponent />
          </div>
        </div>
      </div>
    </>
  );
}

export default ClientTrackerPage;
