import React, { useState } from 'react';
import * as Scroll from 'react-scroll';
import { Link, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'
import PersonIcon from '@material-ui/icons/Person';
import LockIcon from '@material-ui/icons/Lock';
import social_img from '../Static/Img/svg/social_img.svg';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import Tooltip from '@material-ui/core/Tooltip';
import axios from 'axios';

function IndexSection() {
    const [show, setShow] = useState();
    const [message, setMessage] = useState();
    const [userName, setUserName] = useState();
    const [userPassword, setUserPassword] = useState();
    const [error, setError] = useState(false);

    const handleChange = (e) => {
        setUserName({
                [e.target.name]: e.target.value
            })

         setUserPassword({
                [e.target.name]: e.target.value
            })
        }
       const submitLogin= () => {
          //alert(JSON.stringify(value));

          if (userName){
              setError(false);

              const user = {
                name: userName,
                password: userPassword
              };

           // setLoading(true);
            //We call the api
            axios.post(`http://localhost:8011/oauth/token`,{user},{
            headers: {
                'Content-Type': 'application/json',
            }
        }
            )
            .then(function (response) {
                // handle success
                setMessage(response.data.message+response.data.subtitle);
              //  alert("response-->"+JSON.stringify(response.data));
              })
              .catch(function (error) {
                // handle error
                console.log(error);
                setMessage(error);
              })
              .finally(function () {
                // always executed
              });
           // setLoading(false)
        }else{
            setError(true);
        }
        }


    return (
        <>
            <div>
                <div class='container'>
                    <div class='columns'>
                        <div className='column'>
                            <div className='is-centered'>
                                <figure>
                                    <img src={social_img} />
                                </figure>
                            </div>
                        </div>
                        <div class='column is-third-fours'>
                            <div className='is-centered'>
                                <div id='main_index'>
                                    <h1>Hello!!</h1>
                                </div>
                                <small>
                                    <p>
                                        Consectetur cras eu rhoncus netus dignissim hendrerit? Purus aptent scelerisque fusce enim nunc.
                                        Fames sagittis magnis proin lectus accumsan nascetur nisl ad.
                                </p>
                                </small>
                                <br />
                                <div id='secundary_index'>

                                    <button class='button is-primary' onClick={() => setShow(true)}>
                                        Login
                                    </button>

                                </div>

                            </div>
                        </div>
                    </div>
                    <div className='link-to-section'>
                        <div className='is-centered'>

                            <Link to="info_section" spy={true} smooth={true} offset={50} duration={1000} delay={200}>
                                <Tooltip title="Discover More" placement="right">
                                    <ArrowDownwardIcon style={{ fontSize: 120 }} />
                                </Tooltip>
                            </Link>


                        </div>
                    </div>
                </div>

                {/*****************    Login Modal     ***********************/}

                <div className={show ? 'modal is-active' : 'modal'}>
                    <div className="modal-background"></div>
                    <div className="modal-content">
                        <div className='modal-title'>Login</div>
                        <p>
                            Add your user password to login
                    </p>

                        <div className="field">
                            <p className="control has-icons-left has-icons-right">
                                <input className="input" type="text" placeholder="user" />
                                <span className="icon is-small is-left">
                                    <PersonIcon />
                                </span>
                            </p>
                        </div>
                        <div className="field">
                            <p className="control has-icons-left has-icons-right">
                                <input className="input" type="password" placeholder="password" />
                                <span className="icon is-small is-left">
                                    <LockIcon />
                                </span>
                            </p>

                            <p>
                                <button className="button is-white" onClick={() => submitLogin()}>submit</button>
                            </p>
                        </div>

                    </div>
                    <button className="modal-close is-large" aria-label="close" onClick={() => setShow(false)}>

                    </button>
                </div>
            </div>

        </>
    );
}

export default IndexSection;
