import React from 'react';
import { useState, useEffect } from 'react';
import scroller from 'react-scroll';
import NavbarComponent from '../Components/NavbarComponent';
import AsideMenuComponent from '../Components/AsideMenuComponent';
import MUIDataTable from "mui-datatables";
import { useHistory } from "react-router-dom";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CloseIcon from '@material-ui/icons/Close';

const axios = require('axios');

function ClientListPage() {
    const [result, setResult] = useState([]);
    let history = useHistory();

    const goToEdit = (value) => {
        history.push("/edit_client?client=" + value[1]);
    }

    const options = {
        filterType: 'dropdown',
        rowHover: true,
        onRowsDelete: (rowsDeleted) => {
            const rowsToDelete = rowsDeleted.data.map((d) => result[d.dataIndex]);
            handleDelete(rowsToDelete);
        },
        onRowClick: (event, rowData) => {
            goToEdit(event);
        },
        rowsPerPageOptions: [5,10]
    };

    const columns = [


        {
            name: "id",
            label: "ID",
            options: {
                filter: true
            }
        },
        {
            name: "name",
            label: "NAME",
            options: {
                filter: true
            }
        },
        {
            name: "website",
            label: "WEBSITE",
            options: {
                filter: true
            }
        },
        {
            name: "clientManager",
            label: "CLIENT MANAGER",
            options: {
                filter: true,

            }
        },

    ];

    const handleDelete = (recordsToDelete) => {


        var result = recordsToDelete.map(function (item) {
            return item.id;
        }).join(', ');

        axios.delete(`http://localhost:8011/client-management/clients/${result}`, {
            headers: {
                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token')
            }
        }).then(function (response) {
            fetchData();

        })
            .catch(function (error) {

                console.log("Error Fetching Data: "+error);
                history.push("/index");

            })
            .finally(function () {
                // always executed
            });

    }


    const fetchData = async () => {
        axios.get(`http://localhost:8011/client-management/clients`, {
            headers: {

            }

        })
            .then(function (response) {

                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log("Error Fetching Data: "+error);
                history.push("/");


            })
            .finally(function () {
                // always executed
            });


    };
    useEffect(() => { fetchData() }, []);

    return (
        <>
            <div className='container'>
                <NavbarComponent />
            </div>

            <div className='columns'>
                <div className='column'>

                </div>
                <div className='column is-2'>
                    <AsideMenuComponent />
                </div>
                <div className='column is-8'>
                <h2>Clients</h2>
                    <MUIDataTable
                        title={""}
                        data={result}
                        columns={columns}
                        options={options}
                    />
                </div>
                <div className='column'>

                </div>
            </div>
        </>
    );
}
export default ClientListPage;
