import React, { useState, useEffect } from "react";
import TrackerComponent from "./TrackerComponent";
import { useHistory } from "react-router-dom";
import AddOpportunityForm from "./AddOpportunityForm";
import Tooltip from "@material-ui/core/Tooltip";
import OpenInNewIcon from "@material-ui/icons/OpenInNew";
import SpinnerComponent from "../Components/SpinnerComponent";
import AddContactForm from "./AddContactForm";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Chip from "@material-ui/core/Chip";
import ReactHtmlParser from "react-html-parser";
import ClientTrackerChart from "./ClientTrackerChart";
import NavbarComponent from "../Components/NavbarComponent";

const axios = require("axios");

function ClientTrackerPage() {
  const [clientObject, setClientObject] = useState([]);

  let history = useHistory();

  useEffect((props) => {
    //alert(JSON.stringify(value));

    const search = window.location.search;
    const params = new URLSearchParams(search);
    const clientUrl = params.get("client");

    //We call the api
    axios
      .get(
        `${process.env.REACT_APP_API_ROOT_URL}/client-management/clients/${clientUrl}`,
        {
          headers: {
            Authorization: "Bearer " + sessionStorage.getItem("user_token"),
          },
        }
      )
      .then(function (response) {
        setClientObject(response.data);
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
  }, []);

  return (
    <>
      <section>
        <div class="container">
          <NavbarComponent />

          <div class="columns">
            <div class="column is-3">
              {!clientObject.id && <SpinnerComponent />}
              {clientObject.id && (
                <div id="main_index">
                  <h2>
                    {clientObject.name}{" "}
                    <Tooltip
                      title={
                        "Open Client Website (" + clientObject.website + ")"
                      }
                    >
                      <Fab
                        color="primary"
                        aria-label="add"
                        onClick={() => {
                          window.open(clientObject.website, "_blank");
                        }}
                      >
                        <OpenInNewIcon style={{ color: "white" }} />
                      </Fab>
                    </Tooltip>
                  </h2>
                  <div>
                    <Chip
                      label={ReactHtmlParser(
                        "<strong>CLIENT MANAGER</strong> - " +
                          clientObject.clientManager
                      )}
                      variant="outlined"
                    />

                    <Chip
                      label={ReactHtmlParser(
                        "<strong>TIER " +
                          clientObject.tier +
                          "</strong> - " +
                          clientObject.tierDescription
                      )}
                      variant="outlined"
                    />
                  </div>
                </div>
              )}
            </div>
            <div class="column">
            <div class="columns">
              <div class="column">PROCUREMENT STATUS</div>
              <div class="column">LINKEDIN STATUS</div>
              <div class="column">CONTACT STATUS</div>
              <div class="column">ACCOUNT INTELLIGENT STATUS</div>
              <div class="column">PARTNERSHIP STATUS</div>
              <div class="column">OFFERINT STATUS</div>
            </div>
            <div class="columns">
              <div class="column">{clientObject.procurementStatus}</div>
              <div class="column">{clientObject.linkedinStatus}</div>
              <div class="column">{clientObject.contactStatus}</div>
              <div class="column">{clientObject.accountIntellyStatus}</div>
              <div class="column">{clientObject.partnerStatus}</div>
              <div class="column">{clientObject.offeringStatus}</div>
            </div>
            </div>
          </div>
          
          <div class="columns">
            <div class="column">
              <AddContactForm clientId={clientObject.id} />
            </div>
          </div>

          <div class="columns">
            <div class="column">
              <AddOpportunityForm clientId={clientObject.id} />
            </div>
          </div>

          <div class="columns">
            <div class="column">
              <div class="field is-grouped">
                <div class="control">
                  <Tooltip placement="bottom" title="Add New Activity">
                    <Fab
                      variant="extended"
                      color="primary"
                      aria-label="add"
                      onClick={() =>
                        history.push(`/add_track?client=${clientObject.id}`)
                      }
                    >
                      <AddIcon style={{ color: "white" }} />
                      <span style={{ color: "white" }}>New Activity</span>
                    </Fab>
                  </Tooltip>
                </div>
              </div>
            </div>
          </div>
          <center>
            <div class="columns is-centered">
              <div class="column is-three-quarters">
                <TrackerComponent />
              </div>
            </div>
          </center>
        </div>
      </section>
    </>
  );
}

export default ClientTrackerPage;
