import React, { useState, useEffect } from 'react';
import MUIDataTable from "mui-datatables";
import { useHistory } from "react-router-dom";
import Moment from 'react-moment';
import CircularProgress from '@material-ui/core/CircularProgress';

const axios = require('axios');

function TrackerComponent() {
    const [result, setResult] = useState([]);
    let history = useHistory();

    const goToEdit = (value) => {
        history.push("/client_tracker?client=" + value[0]);
    }

    const options = {
        filterType: 'dropdown',
        onRowClick: (event, rowData) => {
            goToEdit(event);
        },
        selectableRows:false,
        download:false,
        filter:false,
        search:false,
        print:false,
        rowsPerPageOptions: [5,10]
    };

    const columns = [


        {
            name: "area_description",
            label: "AREA",
            options: {
                filter: true
            }
        },
        {
            name: "dateAction",
            label: "DATE",
            options: {
                filter: true,
                customBodyRender: value =>
                <Moment>{value}</Moment>

            }
        },
        {
            name: "name",
            label: "NAME",
            options: {
                filter: true,

            }
        },{
            name: "role",
            label: "ROLE",
            options: {
                filter: true,

            }
        },{
            name: "comment",
            label: "COMMENT",
            options: {
                filter: true,

            }
        }

];

    useEffect((props) => {
        //alert(JSON.stringify(value));

        const search = window.location.search;
        const params = new URLSearchParams(search);
        const clientUrl = params.get('client');

        //We call the api
        axios.get(`http://localhost:8011/client-tracker/tracks/${clientUrl}`, {
            headers: {

            }
        }
        )
            .then(function (response) {
                setResult(response.data);


            })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }, []);


    const handleChangeUserObject = (e) => {
        setResult(
            { ...result, [e.target.name]: e.target.value }
        )
    }
    return (
        <>
           <div className="columns is-centered">

           <CircularProgress color="primary" />
           <MUIDataTable
               title={"Actities Tracker"}
               data={result}
               columns={columns}
               options={options}
           />
</div>
        </>
    );
}

export default TrackerComponent;
