import React, { useState } from "react";
const axios = require("axios");
let flag = true;

function AddContactForm(props) {
  const [contactsObject, setContactsObject] = useState([]);
  const [sending, setSending] = useState(false);

  const handleChangeContactObject = (e) => {
    setContactsObject({ ...contactsObject, [e.target.name]: e.target.value });
  };

  const handleSubmit = (evt) => {
    setSending(true);
    flag = true;
    var params = new URLSearchParams();
    let dateValue = document.querySelector("#dateAction").value;


    if (!contactsObject.name) {
      flag = false;
    }
    if (!contactsObject.surname) {
      flag = false;
    }
    if (!contactsObject.role) {
      flag = false;
    }
    if (!contactsObject.comment) {
      flag = false;
    }
    if (!props.clientId) {
      flag = false;
    }

    if (flag) {
        params.append("client_id", props.clientId);

        params.append("surname", contactsObject.surname);
        params.append("name", contactsObject.name);
        params.append("role", contactsObject.role);
        params.append("comment", contactsObject.comment);


      axios
        .post(`http://localhost:8011/client-tracker/tracks`, params, {
          headers: {},
        })
        .then(function (response) {
          history.push(`/client_tracker?client=${props.clientId}`);
        })
        .catch(function (error) {
          // handle error
        })
        .finally(function () {
          // always executed
        });
    }
  };


  return (
    <>
      <div className="columns">
        <div className="column">

          <input
            class="input"
            name="name"
            id="name"
            placeholder="name"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="20"
          />
        </div>
        <div className="column">
          <input
            class="input"
            name="surname"
            id="surname"
            placeholder="surname"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="20"
          />
        </div>
      </div>
      <div className="columns">
        <div className="column">
          <input
            class="input"
            name="role"
            id="role"
            placeholder="role"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="20"
          />
        </div>
        <div className="column">
          <input
            class="input"
            name="comment"
            id="comment"
            placeholder="comment"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="30"
          />
        </div>

      </div>
      <center>
      <button class="button is-black" onClick={() => alert("add contact")}>
         Add Contact
        </button>
        </center>
    </>
  );
}

export default AddContactForm;
