import React, { useState, useEffect } from 'react';
import NavbarComponent from '../Components/NavbarComponent';
import { useHistory } from "react-router-dom";

const axios = require('axios');

function UserAdminPage() {
    const [userObject, setUserObject] = useState([]);

    let history = useHistory();

    useEffect((props) => {
        //alert(JSON.stringify(value));

        const search = window.location.search;
        const params = new URLSearchParams(search);
        const userUrl = params.get('user');

        //We call the api
        axios.get(`http://localhost:8011/users-management/users/${userUrl}`, {
            headers: {
                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token')
            }
        }
        )
            .then(function (response) {
                setUserObject(response.data);


            })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }, []);


    const handleChangeUserObject = (e) => {
        setUserObject(
            { ...userObject, [e.target.name]: e.target.value }
        )
    }

    const handleSubmit = (evt) => {

        var params = new URLSearchParams();
        params.append('username', userObject.username);
        params.append('firstName', userObject.firstName);
        params.append('lastName', userObject.lastName);
        params.append('company', userObject.company);
        params.append('mobile', userObject.mobile);
        params.append('mobile', userObject.mobile);
        params.append('isActive', userObject.isActive);
        params.append('id', userObject.id);

        axios.post(`http://localhost:8011/users-management/users/save`, params, {
            headers: {

                'Authorization': 'Bearer ' + sessionStorage.getItem('user_token')

            }
        }).then(function (response) {
            history.push('/index');

        })
            .catch(function (error) {
                // handle error

            })
            .finally(function () {
                // always executed
            });

    }


    return (
        <>
            <div class='container'>
                <NavbarComponent />
                <nav class="breadcrumb" aria-label="breadcrumbs">
                    <ul>
                        <li><a href="/index">Home</a></li>
                        <li class="is-active"><a href="/admin" aria-current="page">Edit User</a></li>
                    </ul>
                </nav>
                <div id='main_index'>
                    <h2>Edit User</h2>

                </div>
                <div class="card">
                    <div class="card-content">
                        <div class='columns'>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Active</label>
                                    <div class="control">
                                        <div class="select is-primary">
                                            <select name="isActive" id="isActive" onChange={e => handleChangeUserObject(e)} required>
                                                <option>Select</option>
                                                {userObject.isActive === 'Y' &&
                                                    <>
                                                        <option value="Y" selected>Yes</option>
                                                        <option value="N">No</option>
                                                    </>
                                                }
                                                {userObject.isActive === 'N' &&
                                                    <>
                                                        <option value="N" selected>No</option>
                                                        <option value="Y">Yes</option>
                                                    </>
                                                }
                                            </select>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div class='column'>
                                <div class="field">
                                    <label class="label is-primary">Username</label>
                                    <div class="control">
                                        <input class="input is-primary" name="username" id="username" value={userObject.username} type="text" onChange={e => handleChangeUserObject(e)} maxLength='50' required readOnly />
                                        <small>* username is not editable</small>
                                    </div>
                                </div>
                            </div>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Name</label>
                                    <div class="control">
                                        <input class="input is-primary" name="firstName" id="firstName" value={userObject.firstName} type="text" onChange={e => handleChangeUserObject(e)} maxLength='50' required />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div class='columns'>
                            <div class='column'>
                                <div class="field">
                                    <label class="label">Surname</label>
                                    <div class="control">
                                        <input class="input is-primary" name="lastName" id="lastName" value={userObject.lastName} type="text" onChange={e => handleChangeUserObject(e)} maxLength='50' required />
                                    </div>
                                </div>
                            </div>
                            <div class='column'>
                                <div class="field">
                                    <label class="label">Mobile</label>
                                    <div class="control">
                                        <input class="input is-primary" name="mobile" id="mobile" value={userObject.mobile} type="text" onChange={e => handleChangeUserObject(e)} maxLength='50' required />
                                    </div>
                                </div>
                            </div>

                            <div class='column'>
                                <div class="field">
                                    <label class="label">Company</label>
                                    <div class="control">
                                        <input class="input is-primary" name="company" id="company" value={userObject.company} type="text" onChange={e => handleChangeUserObject(e)} maxLength='50' required />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div class='columns is-centered'>
                            <center>
                            <div class='column'>
                                <div class="field is-grouped">
                                    <div class="control">
                                        <button class="button is-text" onClick={() => history.goBack()}>Cancel</button>
                                    </div>
                                    <div class="control">
                                        <button class="button is-primary" onClick={() => handleSubmit()}>Submit</button>
                                    </div>
                                </div>
                            </div>
                            </center>
                        </div>

                    </div>
                </div>

            </div>
        </>
    );
}

export default UserAdminPage;
