import React, { useState, useEffect } from "react";
import NavbarComponent from "../Components/NavbarComponent";
import { useHistory } from "react-router-dom";
const axios = require("axios");

function NewClientPage() {
  const [clientObject, setClientObject] = useState([]);
  const [sending, setSending] = useState(false);
  const [nameVal, setNameVal] = useState(false);
  const [websiteVal, setWebsiteVal] = useState(false);
  const [clientMgnVal, setClientMgnVal] = useState(false);

  let history = useHistory();

  const handleChangeClientObject = (e) => {
    setClientObject({ ...clientObject, [e.target.name]: e.target.value });
  };

  const validateField = (name, website, clientManager) => {
    let result = false;

    if (!name) {
      result = true;
      setNameVal(true);
    }else{
      result = false;
      setNameVal(false);
    }
    if (!website) {
      result = true;
      setWebsiteVal(true);
    }else{
      result = false;
      setWebsiteVal(false);
    }
    if (!clientManager) {
      result = true;
      setClientMgnVal(true);
    }else{
      result = false;
      setClientMgnVal(false);
    }

    return result;
  };

  const handleSubmit = (evt) => {
    setSending(true);

    if (
      validateField(
        clientObject.name,
        clientObject.website,
        clientObject.clientManager
      )
    ) {
    } else {
      var params = new URLSearchParams();
      params.append("name", clientObject.name);
      params.append("website", clientObject.website);
      params.append("clientManager", clientObject.clientManager);

      if (nameVal === true && websiteVal === true && clientMgnVal === true) {
      } else {
        axios
          .post(`http://localhost:8011/client-management/clients`, params, {
            headers: {
              Authorization: "Bearer " + sessionStorage.getItem("user_token"),
            },
          })
          .then(function (response) {
            history.push("/client-mng");
          })
          .catch(function (error) {
            // handle error
            console.log(error);
          })
          .finally(function () {
            // always executed
          });
      }
    }
  };

  return (
    <>
      <div class="container">
        <NavbarComponent />
        <nav class="breadcrumb" aria-label="breadcrumbs">
          <ul>
            <li>
              <a href="/index">Home</a>
            </li>
            <li class="is-active">
              <a href="/new-client" aria-current="page">
                New Client
              </a>
            </li>
          </ul>
        </nav>
        <div id="main_index">
          <h2>New Client</h2>
        </div>
        <div class="card">
          <div class="card-content">
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Name</label>
                  <div class="control">
                    <input
                      class="input"
                      name="name"
                      id="name"
                      type="text"
                      placeholder="name"
                      onChange={(e) => handleChangeClientObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && nameVal && (
                      <small>
                        <span className="validation">Mandatory Field</span>
                      </small>
                    )}
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Website</label>
                  <div class="control">
                    <input
                      class="input"
                      name="website"
                      id="website"
                      type="text"
                      placeholder="website"
                      onChange={(e) => handleChangeClientObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && websiteVal && (
                      <small>
                        <span className="validation">Mandatory Field</span>
                      </small>
                    )}
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Client Manager</label>
                  <div class="control">
                    <input
                      class="input"
                      name="clientManager"
                      id="clientManager"
                      type="text"
                      placeholder="name"
                      onChange={(e) => handleChangeClientObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && clientMgnVal && (
                      <small>
                        <span className="validation">Mandatory Field</span>
                      </small>
                    )}
                  </div>
                </div>
              </div>
            </div>

            <div class="columns is-centered">
              <center>
                <div class="column">
                  <div class="field is-grouped">
                    <div class="control">
                      <button
                        class="button is-text"
                        onClick={() => history.goBack()}
                      >
                        Cancel
                      </button>
                    </div>
                    <div class="control">
                      <button
                        class="button is-primary"
                        onClick={() => handleSubmit()}
                      >
                        Submit
                      </button>
                    </div>
                  </div>
                </div>
              </center>
            </div>
          </div>
        </div>
      </div>
      <br />
      <br />
      <br />
    </>
  );
}

export default NewClientPage;
