import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Alert from "@material-ui/lab/Alert";
import DeleteIcon from "@material-ui/icons/Delete";

const axios = require("axios");
let flag = true;

function AddContactForm(props) {
  const [contactsObject, setContactsObject] = useState([]);
  const [sending, setSending] = useState(false);

  let history = useHistory();

  const handleChangeContactObject = (e) => {
    setContactsObject({ ...contactsObject, [e.target.name]: e.target.value });
  };

  const handleSubmit = (evt) => {

    setSending(true);

    flag = true;
    var params = new URLSearchParams();

    if (!contactsObject.name) {
      flag = false;
    }
    if (!contactsObject.surname) {
      flag = false;
    }
    if (!contactsObject.role) {
      flag = false;
    }
    if (!contactsObject.comment) {
      flag = false;
    }
    if (!props.clientId) {
      flag = false;
    }

    if (flag) {
        params.append("client_id", props.clientId);

        params.append("surname", contactsObject.surname);
        params.append("name", contactsObject.name);
        params.append("role", contactsObject.role);
        params.append("comment", contactsObject.comment);


      axios
        .post(`http://localhost:8011/contact-management/contacts`, params, {
          headers: {},
        })
        .then(function (response) {
            history.push(`/`);
            history.push(`/client_tracker?client=${props.clientId}`);
        })
        .catch(function (error) {
          // handle error
        })
        .finally(function () {
          // always executed
        });
    }
  };
  const handleDeleteContacts = (id) => {

    axios
       .delete(`http://localhost:8011/contact-management/contacts/${id}`, {
         headers: {},
       })
       .then(function (response) {
         history.push(`/`);
         history.push(`/client_tracker?client=${clientObject.id}`)
       })
       .catch(function (error) {
         // handle error
       })
       .finally(function () {
         // always executed
       });

 };

  return (
    <>

      <div className="columns">
        <div className="column">

          <input
            class="input"
            name="name"
            id="name"
            placeholder="name"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="20"
          />

        {sending && !contactsObject.name && (
                        <>
                          <Alert variant="outlined" severity="error">
                            Mandatory Field
                          </Alert>
                        </>
                      )}
                       </div>
        <div className="column">
          <input
            class="input"
            name="surname"
            id="surname"
            placeholder="surname"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="20"
          />

        {sending && !contactsObject.surname  && (
                        <>
                          <Alert variant="outlined" severity="error">
                            Mandatory Field
                          </Alert>
                        </>
                      )}
      </div>
      </div>
      <div className="columns">
        <div className="column">
          <input
            class="input"
            name="role"
            id="role"
            placeholder="role"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="20"
          />

        {sending && !contactsObject.role  && (
                        <>
                          <Alert variant="outlined" severity="error">
                            Mandatory Field
                          </Alert>
                        </>
                      )}
                      </div>
        <div className="column">
          <input
            class="input"
            name="comment"
            id="comment"
            placeholder="comment"
            type="text"
            onChange={(e) => handleChangeContactObject(e)}
            maxLength="30"
          />

        {sending && !contactsObject.comment  && (
                        <>
                          <Alert variant="outlined" severity="error">
                            Mandatory Field
                          </Alert>
                        </>
                      )}
      </div>
      </div>
      <center>

      <button class="button is-black" onClick={() => handleSubmit()}>
         Add Contact
        </button>
        </center>
        <center>
                  <table className="table is-hoverable">
                    <thead>
                      <td></td>
                      <td>Name</td>
                      <td>Surname</td>
                      <td>Role</td>
                      <td>Comment</td>
                    </thead>
                    <tbody>
                      {contactsObject.map(function (item) {
                        return (
                          <tr>
                            <td>
                              <button
                                class="button is-text"
                                onClick={() => handleDeleteContacts(item.id)}
                              >
                                <DeleteIcon />
                              </button>
                            </td>

                            <td>{item.name}</td>
                            <td>{item.surname}</td>
                            <td>{item.role}</td>
                            <td>{item.comment}</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </center>

    </>
  );
}

export default AddContactForm;
