import React, { useState } from 'react';
import * as Scroll from 'react-scroll';
import { Link, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'
import PersonIcon from '@material-ui/icons/Person';
import { useHistory } from "react-router-dom";
import LockIcon from '@material-ui/icons/Lock';
import social_img from '../Static/Img/svg/social_img.svg';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import Tooltip from '@material-ui/core/Tooltip';
import axios from 'axios';
import cookie from 'react-cookies'

function IndexSection() {
    const [show, setShow] = useState();
    const [message, setMessage] = useState();
    const [userName, setUserName] = useState();
    const [userPassword, setUserPassword] = useState();
    const [error, setError] = useState(false);

    let history = useHistory();

    const handleChangeUser = (e) => {
        setUserName(
            e.target.value
        )
    }
    const handleChangePassword = (e) => {
        setUserPassword(
            e.target.value
        )
    }
    const submitLogin = () => {


        if (userName) {
            setError(false);


            const body = new FormData();  // how can I do this not as a form?
            body.append('grant_type', 'password');
            body.append('username', userName);
            body.append('password', userPassword);
            // setLoading(true);
            //We call the api
            axios.post(`http://localhost:8011/oauth/token`, body, {
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': 'Basic cG9ydGFsLWNsaTpwYXNzd29yZA==',
                    //'Access-Control-Allow-Origin' : '*',
                    //'Access-Control-Allow-Headers' : 'Content-Type,X-Requested-With, content-type, access-control-allow-origin, access-control-allow-methods, access-control-allow-headers',
                }

            }
            )
                .then(function (response) {
                    // handle success
                    // setMessage(response.data.message + response.data.subtitle);
                    /*cookie.save("user_token",response,{
                        path: '/',
                        expires:response.expires_in,
                        maxAge: response.expires_in,
                        secure: true,
                        httpOnly: true
                      })*/
                      sessionStorage.setItem("user_token",JSON.stringify(response))
                      sessionStorage.setItem("username",userName)
                   history.push("/index");
                })
                .catch(function (error) {
                    // handle error
                    console.log(error);
                    setMessage(error);
                })
                .finally(function () {
                    // always executed
                });
            // setLoading(false)
        } else {
            setError(true);
        }
    }


    return (
        <>
            <div>
                <div class='container'>
                    <div class='columns'>
                        <div className='column'>
                            <div className='is-centered'>
                                <figure>
                                    <img src={social_img} />
                                </figure>
                            </div>
                        </div>
                        <div class='column is-third-fours'>
                            <div className='is-centered'>
                                <div id='main_index'>
                                    <h1>Hello!!</h1>
                                </div>
                                <small>
                                    <p>
                                        Consectetur cras eu rhoncus netus dignissim hendrerit? Purus aptent scelerisque fusce enim nunc.
                                        Fames sagittis magnis proin lectus accumsan nascetur nisl ad.
                                </p>
                                </small>

                                <br />

                                <div class="field is-grouped">

                                    <Link className='button is-link' to="users_section" spy={true} smooth={true} offset={50} duration={1000} delay={200}>
                                        Search Users
                                    </Link>

                                    <button className='button is-primary' onClick={() => setShow(true)}>
                                        Admin
                                    </button>

                                </div>

                            </div>
                        </div>
                    </div>
                    <div className='link-to-section'>
                        <div className='is-centered'>

                            <Link to="info_section" spy={true} smooth={true} offset={50} duration={1000} delay={200}>
                                <Tooltip title="Discover More" placement="right">
                                    <ArrowDownwardIcon style={{ fontSize: 120 }} />
                                </Tooltip>
                            </Link>


                        </div>
                    </div>
                </div>

                {/*****************    Login Modal     ***********************/}

                <div className={show ? 'modal is-active' : 'modal'}>
                    <div className="modal-background"></div>
                    <div className="modal-content">
                        <div className='modal-title'>Login</div>
                        <p>
                            Add your user password to login
                    </p>

                        <div className="field">
                            <p className="control has-icons-left has-icons-right">
                                <input className="input" type="email" placeholder="user" onChange={e => handleChangeUser(e)} />
                                <span className="icon is-small is-left">
                                    <PersonIcon />
                                </span>
                            </p>
                        </div>
                        <div className="field">
                            <p className="control has-icons-left has-icons-right">
                                <input className="input" type="password" placeholder="password" onChange={e => handleChangePassword(e)} />
                                <span className="icon is-small is-left">
                                    <LockIcon />
                                </span>
                            </p>

                            <p>
                                <button className="button is-white" onClick={() => submitLogin()}>submit</button>
                            </p>
                        </div>

                    </div>
                    <button className="modal-close is-large" aria-label="close" onClick={() => setShow(false)}>

                    </button>
                </div>
            </div>

        </>
    );
}

export default IndexSection;
