import React, { useState, useEffect } from "react";
import NavbarComponent from "../Components/NavbarComponent";
import { useHistory } from "react-router-dom";
import ErrorIcon from "@material-ui/icons/Error";

const axios = require("axios");

let flag = true;

function NewUserPage() {
  const [userObject, setUserObject] = useState([]);
  const [sending, setSending] = useState(false);

  let history = useHistory();

  const handleChangeUserObject = (e) => {
    setUserObject({ ...userObject, [e.target.name]: e.target.value });
  };

  const handleSubmit = (evt) => {
    setSending(true);
    flag = true;

    if (!userObject.username) {
      flag = false;
    }
    if (!userObject.firstName) {
      flag = false;
    }
    if (!userObject.lastName) {
      flag = false;
    }
    if (!userObject.company) {
      flag = false;
    }
    if (!userObject.mobile) {
      flag = false;
    }
    if (!userObject.isActive) {
      flag = false;
    }
    if (!userObject.password) {
      flag = false;
    }
    if (!userObject.confirmPassword) {
      flag = false;
    }

    var params = new URLSearchParams();
    params.append("username", userObject.username);
    params.append("firstName", userObject.firstName);
    params.append("lastName", userObject.lastName);
    params.append("company", userObject.company);
    params.append("mobile", userObject.mobile);
    params.append("isActive", userObject.isActive);
    params.append("password", userObject.password);
    params.append("confirmPassword", userObject.confirmPassword);

    if (flag) {
    axios
      .post(`http://localhost:8011/users-management/users/save`, params, {
        headers: {
          Authorization: "Bearer " + sessionStorage.getItem("user_token"),
        },
      })
      .then(function (response) {
        history.push("/index");
      })
      .catch(function (error) {
        // handle error
      })
      .finally(function () {
        // always executed
      });
    }
  };

  return (
    <>
      <div class="container">
        <NavbarComponent />
        <nav class="breadcrumb" aria-label="breadcrumbs">
          <ul>
            <li>
              <a href="/index">Home</a>
            </li>
            <li class="is-active">
              <a href="/new-user" aria-current="page">
                New User
              </a>
            </li>
          </ul>
        </nav>
        <div id="main_index">
          <h2>New User</h2>
        </div>
        <div class="card">
          <div class="card-content">
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Active</label>
                  <div class="control">
                    <div class="select">
                      <select
                        name="isActive"
                        id="isActive"
                        onChange={(e) => handleChangeUserObject(e)}
                        required
                      >
                        <option selected value="">
                          Select
                        </option>
                        <option value="Y">Yes</option>
                        <option value="N">No</option>
                      </select>
                      {sending && !userObject.isActive && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                    </div>
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Username</label>
                  <div class="control">
                    <input
                      class="input"
                      name="username"
                      id="username"
                      type="text"
                      placeholder="username"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.username && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Name</label>
                  <div class="control">
                    <input
                      class="input"
                      name="firstName"
                      id="firstName"
                      type="text"
                      placeholder="name"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.firstName && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>
            </div>
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Surname</label>
                  <div class="control">
                    <input
                      class="input"
                      name="lastName"
                      id="lastName"
                      type="text"
                      placeholder="surname"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.lastname && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>

              <div class="column">
                <div class="field">
                  <label class="label">Mobile</label>
                  <div class="control">
                    <input
                      class="input"
                      name="mobile"
                      id="mobile"
                      type="text"
                      placeholder="mobile"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.mobile && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Company</label>
                  <div class="control">
                    <input
                      class="input"
                      name="company"
                      id="company"
                      type="text"
                      placeholder="company"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.company && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>
            </div>
            <div class="columns">
              <div class="column">
                <div class="field">
                  <label class="label">Password</label>
                  <div class="control">
                    <input
                      class="input"
                      name="password"
                      id="password"
                      type="password"
                      placeholder="password"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.password && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>
              <div class="column">
                <div class="field">
                  <label class="label">Confirm Password</label>
                  <div class="control">
                    <input
                      class="input"
                      name="confirmPassword"
                      id="confirmPassword"
                      type="password"
                      placeholder="confirm password"
                      onChange={(e) => handleChangeUserObject(e)}
                      maxLength="50"
                      required
                    />
                    {sending && !userObject.confirmPassword && (
                        <small>
                            <span className="validation">Mandatory Field<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                      {userObject.password && userObject.confirmPassword && userObject.password !== userObject.confirmPassword  (
                        <small>
                            <span className="validation">Password Doesn´t Match<ErrorIcon fontSize="small"/></span>
                        </small>
                      )}
                  </div>
                </div>
              </div>
            </div>
            <div class="columns is-centered">
              <center>
                <div class="column">
                  <div class="field is-grouped">
                    <div class="control">
                      <button
                        class="button is-text"
                        onClick={() => history.goBack()}
                      >
                        Cancel
                      </button>
                    </div>
                    <div class="control">
                      <button
                        class="button is-primary"
                        onClick={() => handleSubmit()}
                      >
                        Submit
                      </button>
                    </div>
                  </div>
                </div>
              </center>
            </div>
          </div>
        </div>
      </div>
      <br />
      <br />
      <br />
    </>
  );
}

export default NewUserPage;
