import React, { useState, useEffect } from 'react';
import MUIDataTable from "mui-datatables";
import { useHistory } from "react-router-dom";
import DeleteIcon from "@material-ui/icons/Delete";
import ReactHtmlParser from 'react-html-parser';


const axios = require('axios');

function TrackerComponent() {
    const [result, setResult] = useState([]);
    let history = useHistory();

   

    const options = {
        filterType: 'dropdown',
        onRowsDelete: (rowsDeleted) => {
            const rowsToDelete = rowsDeleted.data.map((d) => result[d.dataIndex]);
            handleDelete(rowsToDelete);
        },
        download:false,
        filter:false,
        search:false,
        print:false,
        rowsPerPageOptions: [5,10]
    };

    const columns = [

        {
            name: "id",
            label: "ID",
            options: {
                filter: false,
                searchable:false,
                display:false,
                view:false
               
            }
        },
        {
            name: "area_description",
            label: "AREA",
            options: {
                filter: true
            }
        },
        {
            name: "dateAction",
            label: "DATE",
            options: {
                filter: true
      
            }
        },
        {
            name: "name",
            label: "NAME",
            options: {
                filter: true,
           
            }
        },{
            name: "role",
            label: "ROLE",
            options: {
                filter: true,
           
            }
        },{
            name: "comment",
            label: "COMMENT",
            options: {
                filter: true,
                customBodyRender: (value) => {
                    return (
                   ReactHtmlParser(value)
                    );
                },
                
            }
        }
  
];

useEffect(() => { fetchData() }, []);

        const fetchData = async () => {
        //alert(JSON.stringify(value));
        
        const search = window.location.search;
        const params = new URLSearchParams(search);
        const clientUrl = params.get('client');
       
        //We call the api
        axios.get(`${process.env.REACT_APP_API_ROOT_URL}/client-tracker/tracks/${clientUrl}`, {
            headers: {
                Authorization: "Bearer " + sessionStorage.getItem("user_token"),
              }
        }
        )
            .then(function (response) {
                setResult(response.data);
                

            })
            .catch(function (error) {
                // handle error
            })
            .finally(function () {
                // always executed
               
            });

    }

    const handleDelete = (activityId) => {
        const search = window.location.search;
        const params = new URLSearchParams(search);
        const clientUrl = params.get('client');
       

      /*  var result = recordsToDelete.map(function (item) {
            return item.id;
        }).join(', ');
        */

        axios.delete(`${process.env.REACT_APP_API_ROOT_URL}/client-tracker/tracks/${activityId}`, {
            headers: {}
        }).then(function (response) {
           // history.push("/");
             //   history.push("/client_tracker?client="+clientUrl);
             fetchData();

        }).catch(function (error) {
                
                console.log("Error Fetching Data: "+error);
               

            })
            .finally(function () {
                // always executed
            });

    }
   
    return (
        <>
           <div className="columns is-centered">


           <table className="table is-hoverable">
              <thead>
                <td></td>
                <td>Area</td>
                <td>Date</td>
                <td>Name</td>
                <td>Role</td>
                <td>Comment</td>
              </thead>
              <tbody>
                {result.length > 0 ? (
                  result.map(function (item) {
                    return (
                      <tr>
                        <td>
                          <button
                            class="button is-text"
                            onClick={() => handleDelete(item.id)}
                          >
                            <DeleteIcon />
                          </button>
                        </td>

                        <td>{item.area_description}</td>
                        <td>{item.dateAction}</td>
                        <td>{item.name}</td>
                        <td>{item.role}</td>
                        <td>{item.comment}</td>
                      </tr>
                    );
                  })
                ) : (
                  <td colspan="6">
                    <center>No results found</center>
                  </td>
                )}
              </tbody>
            </table>

        {/*
           <MUIDataTable
               title={"Actities Tracker"}
               data={result}
               columns={columns}
               options={options}
           />
        */}
</div>
        </>
    );
}

export default TrackerComponent;
