import React from "react";
import Drawer from "@material-ui/core/Drawer";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Fab from "@material-ui/core/Fab";
import List from "@material-ui/core/List";
import GroupAddIcon from '@material-ui/icons/GroupAdd';
import ListItem from "@material-ui/core/ListItem";
import Divider from "@material-ui/core/Divider"
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import GroupIcon from '@material-ui/icons/Group';
import PeopleOutlineIcon from '@material-ui/icons/PeopleOutline';
import KeyboardArrowRightIcon from "@material-ui/icons/KeyboardArrowRight";
import Tooltip from "@material-ui/core/Tooltip";
import PersonOutlineIcon from '@material-ui/icons/PersonOutline';
import logo_everis from "../Static/Img/images/logo-everis.png";

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
});

function AsideDrawerComponent() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === "top" || anchor === "bottom",
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
        <figure class="image">
         <img src={logo_everis} />
        </figure>
      <List>
        <ListItem button="true">
          <ListItemIcon><GroupIcon /></ListItemIcon>
          <ListItemText primary={"Users List"} />
        </ListItem>
        <ListItem button="true">
          <ListItemIcon><GroupAddIcon /></ListItemIcon>
          <ListItemText primary={"New Users"} />
        </ListItem>
        <Divider />
        <ListItem button="true">
          <ListItemIcon><PeopleOutlineIcon /></ListItemIcon>
          <ListItemText primary={"Clients List"} />
        </ListItem>
        <ListItem button="true">
          <ListItemIcon><PersonOutlineIcon /></ListItemIcon>
          <ListItemText primary={"New List"} />
        </ListItem>
      </List>
    </div>
  );
  return (
    <>
      <div>
        {["left"].map((anchor) => (
          <React.Fragment key={anchor}>
            <Tooltip title="Display Menu">
              <Fab color="primary" onClick={toggleDrawer(anchor, true)}>
                <KeyboardArrowRightIcon
                  style={{ color: "white", fontSize: "40px" }}
                />
              </Fab>
            </Tooltip>
            <Drawer
              anchor={anchor}
              open={state[anchor]}
              onClose={toggleDrawer(anchor, false)}
            >
              {list(anchor)}
            </Drawer>
          </React.Fragment>
        ))}
      </div>
    </>
  );
}

export default AsideDrawerComponent;
